CREATE TABLE AMINOACIDS(
    AMINOACID_ID NUMBER(2) GENERATED BY DEFAULT AS IDENTITY NOT NULL,
    CODON VARCHAR2(3) NOT NULL,
    AMINOACID_SYMBOL VARCHAR2(1) NOT NULL,
    AMINOACID_NAME VARCHAR2(255) NOT NULL,
    AMINOACID_ABREVIATION VARCHAR2(255) NOT NULL
);

ALTER TABLE AMINOACIDS ADD CONSTRAINT aminoacid_id_pk PRIMARY KEY (AMINOACID_ID);

-- Clave for√°nea en Aminoacidos hacia Moleculas (1,1)
ALTER TABLE AMINOACIDS ADD CONSTRAINT aminoacids_molecule_fk FOREIGN KEY (MOLECULE_ID) REFERENCES MOLECULES(MOLECULE_ID);


COMMENT ON TABLE AMINOACIDS IS 'Amino acids and their corresponding CODONs.';

