CREATE TABLE ELEMENTS (
    ELEMENT_ID NUMBER(3) GENERATED ALWAYS AS IDENTITY NOT NULL,
    ELEMENT_NAME VARCHAR2(255) NOT NULL,
    ELEMENT_SYMBOL VARCHAR2(2) NOT NULL,
    ELEMENT_ATOMIC_MASS NUMBER NOT NULL,
    ELEMENT_PROTONS NUMBER(3) NOT NULL,
    ELEMENT_NEUTRONS NUMBER(3) NOT NULL
);

COMMENT ON TABLE ELEMENTS IS 'Periodic Table.';

ALTER TABLE ELEMENTS ADD CONSTRAINT element_id_pk PRIMARY KEY (ELEMENT_ID);


